mapper.update:
update CONTENT
set TITLE= #{title}, BODY = #{body}, UPDATED_DATE = sysdate, USER_EMAIL=#{userEmail}, CATEGORY_ID=#{categoryId}
where CONTENT_ID = #{contentId}

mapper.select:

select CONTENT_ID, TITLE, BODY, CREATED_DATE, UPDATED_DATE, USER_EMAIL
from 
	(
	select /*+ INDEX_DESC(content content_pk) */
	  rownum RN, CONTENT_ID, TITLE, BODY, CREATED_DATE, UPDATED_DATE, CATEGORY_ID, USER_EMAIL
	from 
	  content
	where CATEGORY_ID = #{category} and rownum <= #{pageNum} * #{amount}
	)
where rn > (#{pageNum} -1) * #{amount}
